        else if (ideologyId == Faction.IdeologyID.bureaucracy)
        {
            language = (i < 30) ? languages.spanish : (i < 60) ? languages.italian : (i < 80) ? languages.oldenglish : (i < 90) ? languages.greek : languages.latin;
