    static string[] getList(languages language, bool isMale)
    {
        switch (language)
        {
            case languages.latin:
                return isMale ? latinM : latinF;
            case languages.greek:
                return isMale ? greekM : greekF;
            case languages.italian:
                return isMale ? italianM : italianF;
            case languages.spanish:
                return isMale ? spanishM : spanishF;
            case languages.oldenglish:
                return isMale ? oldenglishM : oldenglishF;
            default:
                Debug.LogWarning("no language");
                return latinM;
        }
    }
